mapper.update:
update ${tableName}
set port_title = #{name,jdbcType=VARCHAR},
  dataType = #{datatype,jdbcType=VARCHAR},
  dataVersion = #{dataversion,jdbcType=VARCHAR},
  useides = #{useides,jdbcType=VARCHAR},
  port_state = #{portState,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=DATE}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, port_title, dataType, dataVersion, useides, experience, user_account, is_essence,
port_state, is_top, reply_number, look_number, create_time, delete_flag
from ${tableName}
where id = #{id,jdbcType=BIGINT}

select id, port_title, dataType, dataVersion, useides, experience, user_account, is_essence,
port_state, is_top, reply_number, look_number, create_time, delete_flag
from ${tableName}
