mapper.delete:
delete from user_message


    and receive_alias = #{alias}


    and class_id = #{classId}


    and details_id = #{detailsId}


    and port_id = #{portId}

mapper.insert:
insert into user_message (class_id, port_id, details_id, 
  src_alias, receive_alias, receive_date, auth_flag
  )
values (#{classId,jdbcType=TINYINT}, #{portId,jdbcType=BIGINT}, #{detailsId,jdbcType=BIGINT}, 
  #{srcAlias,jdbcType=VARCHAR}, #{receiveAlias,jdbcType=VARCHAR}, #{receiveDate,jdbcType=DATE}
  , #{authFlag,jdbcType=BIT}
  )
